// frontend/src/App.jsx

import React, { useState, useEffect, useCallback } from 'react';
import { fetchJobs, triggerScrape } from './api';
import Filters from './components/Filters';
import JobsTable from './components/JobsTable';
import Charts from './components/Charts';
import MapView from './components/MapView';

export default function App() {
  const [jobs, setJobs] = useState([]);
  const [filters, setFilters] = useState({ title: '', employer: '', area: '' });

  const loadJobs = useCallback(async () => {
    try {
      const data = await fetchJobs(filters);
      setJobs(data);
    } catch (err) {
      console.error('Ошибка при загрузке вакансий:', err);
    }
  }, [filters]);

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(() => {
    loadJobs();
  }, [loadJobs]);

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  const handleScrape = async () => {
    try {
      await triggerScrape();
      loadJobs(); // обновляем после парсинга
    } catch (err) {
      console.error('Ошибка при парсинге:', err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Job Dashboard</h1>
      <button onClick={handleScrape}>Scrape Jobs</button>
      <Filters filters={filters} onChange={handleFilterChange} />
      <JobsTable jobs={jobs} />
      <Charts jobs={jobs} />
      <MapView />
    </div>
  );
}
