// frontend/src/api.js

// Жёстко фиксируем адрес вашего FastAPI-сервера
const API_URL = "http://localhost:8000";

/**
 * Получить список вакансий с бэкенда
 * @param {{ title?: string, employer?: string, area?: string }} params 
 */
export async function fetchJobs(params) {
  const query = new URLSearchParams(params).toString();
  const res = await fetch(`${API_URL}/api/jobs?${query}`);
  if (!res.ok) {
    throw new Error(`Ошибка при получении вакансий: ${res.status}`);
  }
  return res.json();
}

/** 
 * Триггер парсинга: POST /api/scrape 
 */
export async function triggerScrape() {
  const res = await fetch(`${API_URL}/api/scrape`, {
    method: 'POST',
  });
  if (!res.ok) {
    throw new Error(`Ошибка при парсинге: ${res.status}`);
  }
  return res.json();
}
