import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

export default function D3BarChart({
  data,
  labelKey,
  valueKey,
  color,
  showLegend = false
}) {
  const svgRef = useRef();

  useEffect(() => {
    if (!Array.isArray(data) || data.length === 0) return;

    const margin = { top: 20, right: 20, bottom: 60, left: 60 };
    const barWidth = 40; // ширина «ячейки» на один бар
    const width = margin.left + margin.right + data.length * barWidth;
    const height = 400;

    // базовая настройка SVG
    const svg = d3
      .select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    // очистка перед рисованием
    svg.selectAll('*').remove();

    // шкалы
    const x = d3
      .scaleBand()
      .domain(data.map((d) => d[labelKey]))
      .range([margin.left, width - margin.right])
      .padding(0.1);

    const y = d3
      .scaleLinear()
      .domain([0, d3.max(data, (d) => d[valueKey])])
      .nice()
      .range([height - margin.bottom, margin.top]);

    // бруски
    svg
      .append('g')
      .selectAll('rect')
      .data(data)
      .join('rect')
        .attr('x', (d) => x(d[labelKey]))
        .attr('y', (d) => y(d[valueKey]))
        .attr('width', x.bandwidth())
        .attr('height', (d) => y(0) - y(d[valueKey]))
        .attr('fill', color || '#ff69b4');

    // ось Y
    svg
      .append('g')
      .attr('transform', `translate(${margin.left},0)`)
      .call(d3.axisLeft(y).ticks(5));

    // ось X с цифрами
    svg
      .append('g')
      .attr('transform', `translate(0,${height - margin.bottom})`)
      .call(
        d3
          .axisBottom(x)
          .tickFormat((_, i) => i + 1)
          .tickSize(0)
      )
      .selectAll('text')
        .style('font-size', '12px');

  }, [data, labelKey, valueKey, color]);

  return (
    <div style={{ overflowX: 'auto' }}>
      <svg ref={svgRef} style={{ display: 'block' }} />
      {showLegend && (
        <div style={{ marginTop: 12, fontSize: '14px' }}>
          <strong>Legend:</strong>
          <ol style={{ margin: '8px 0 0 16px' }}>
            {data.map((d, i) => (
              <li key={i}>
                {i + 1}: {d[labelKey]}
              </li>
            ))}
          </ol>
        </div>
      )}
    </div>
  );
}
