import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, CircleMarker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

export default function MapPage() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/summary')
      .then(res => res.json())
      .then(setData)
      .catch(err => console.error('Error fetching summary:', err));
  }, []);

  const max = Math.max(...data.map(d => d.count), 1);

  return (
    <div>
      <h2 className="text-2xl mb-4">Интерактивная карта вакансий</h2>
      <MapContainer center={[55.75, 37.62]} zoom={4} style={{ height: '500px', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {data.map(c => (
          <CircleMarker
            key={c.city}
            center={[c.lat, c.lon]}
            radius={5 + (c.count / max) * 20}
            pathOptions={{ color: '#ff69b4' }}
            fillOpacity={0.6}
          >
            <Popup>
              <b>{c.city}</b>: {c.count} вакансий<br/>
              <a href={`https://hh.ru/search/vacancy?area=${c.area_id}`} target="_blank" rel="noreferrer">Просмотр</a>
            </Popup>
          </CircleMarker>
        ))}
      </MapContainer>
    </div>
  );
}
